import React from 'react'

const ItemListContainer = (props) => {

  // let {mensajeTemporal } = props;

  const todoTaskRef = useRef();
  const [todos, setTodos] = useState([
    { id: 1, task: "Tarea ", completed: false },
  ]);

  useEffect(() => {
    const storedTodos = JSON.parse(localStorage.getItem(KEY));
    if (storedTodos) {
      setTodos(storedTodos);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem(KEY, JSON.stringify(todos));
  }, [todos]);

  const toggleTodo = (id) => {
    const newTodos = [...todos];
    const todo = newTodos.find((todo) => todo.id === id);
    todo.completed = !todo.completed;
    setTodos(newTodos);
  };

  const handleTodoAdd = (event) => {
    const task = todoTaskRef.current.value;
    if (task === "") return;

    setTodos((prevTodos) => {
      return [...prevTodos, { id: uuidv4(), task, completed: false }];
    });

    todoTaskRef.current.value = null;
  };

  const handleClearAll = () => {
    const newTodos = todos.filter((todo) => !todo.completed);
    setTodos(newTodos);
  };



  return (
    <>
       <div className="ItemListContainer">ItemListContainer : -------- {props.mensajeTemporal} por ahora solo para el desafio #02 :  {props.coderHouse}</div>



       <TodoList todos={todos} toggleTodo={toggleTodo} />
        <div> Probando Fragment</div>
        <input ref={todoTaskRef} type="text" placeholder="Nueva tarea" />
        <button onClick={handleTodoAdd}>AÃ±adir</button>
        <button onClick={handleClearAll}>Eliminar</button>
        <div>
          Te quedan {todos.filter((todo) => !todo.completed).length} tareas por
          terminar
        </div>




     </>

  )
}

export default ItemListContainer
